const Services = () => {
  const containerRef = useRef(null);
  const sectionsRef = useRef([]); // Array of refs for each section
  const [activeSection, setActiveSection] = useState(0);
  const leftColumnRef = useRef(null);
  const isInView = useInView(leftColumnRef, { once: true });

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = sectionsRef.current.indexOf(entry.target);
            if (index !== -1) {
              setActiveSection(index);
            }
          }
        });
      },
      {
        root: null, // Uses the viewport as the root
        threshold: 1, // Section is "active" when 50% in view
      }
    );

    sectionsRef.current.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => {
      sectionsRef.current.forEach((section) => {
        if (section) observer.unobserve(section);
      });
    };
  }, []);

  return (
    <div className="bg-services min-h-[100vh]" ref={containerRef}>
      <div className="container mx-auto">
        <div className="grid grid-cols-2 gap-12">
          {/* Left Column - Services */}
          <motion.div
            ref={leftColumnRef}
            className="sticky top-20 h-[80vh] flex flex-col justify-center"
            initial={{ opacity: 0, x: -100 }}
            animate={isInView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.6 }}
          >
            <h1 className="heading text-4xl font-bold text-white mb-6">
              Transforming Ideas into Digital Excellence
            </h1>
            <p className="text-white/70">
              At Safinia Agency, we specialize in delivering cutting-edge IT
              solutions tailored to meet your business needs. From innovative
              designs to robust development, our services are designed to
              empower your brand and ensure your digital presence stands out.
              Partner with us to turn your vision into a reality.
            </p>
            <ul className="text-white mt-10 space-y-4">
              {services.map((service, index) => (
                <li
                  key={index}
                  className={`flex gap-3 text-xl transition-all duration-300 ${
                    activeSection === index ? "text-primary" : "text-white"
                  }`}
                >
                  <span className="my-auto text-primary">
                    <GiCheckMark />
                  </span>
                  {service.name}
                </li>
              ))}
            </ul>
          </motion.div>

          {/* Right Column - Scrollable Sections */}
          <div>
            <ul className="text-white space-y-10">
              {services.map((service, index) => (
                <motion.li
                  key={index}
                  ref={(el) => (sectionsRef.current[index] = el)} // Save refs to array
                  className="h-[70vh] flex items-center justify-center"
                  initial={{ opacity: 0.5 }}
                  animate={{
                    opacity: activeSection === index ? 1 : 0.5,
                  }}
                  transition={{ duration: 0.4 }}
                >
                  {service.icon}
                </motion.li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;